const formatTime=t=>{if(isNaN(t))return"00:00";const r=Math.floor(t/3600),o=Math.floor(t%3600/60),a=Math.floor(t%60);return(r>0?r.toString().padStart(2,"0")+":":"")+o.toString().padStart(2,"0")+":"+a.toString().padStart(2,"0")},formatFileSize=(t,r=1)=>{const o=["B","KB","MB","GB","TB"];let a=0;for(;t>=1024&&a<o.length-1;)t/=1024,a++;return`${new Intl.NumberFormat("en-US",{maximumFractionDigits:r}).format(t)} ${o[a]}`};