class VideoPlayer{constructor(){this.player=document.querySelector(".video-wrapper"),this.video=this.player.querySelector("video"),this.videoId=this.player.getAttribute("videoId"),this.videoDir="/media/"+this.videoId+"/",this.videoData={},this.loaded=!1,this.ready=!1,this.loadMeta().then(this.initVideo.bind(this))}async loadMeta(){if(!this.loaded)try{const e=await fetch("/api/video/"+this.videoId,{method:"post",headers:{"Content-Type":"application/json"}});if(!e.ok)throw new Error("Error fetching video data");this.videoData=await e.json(),this.loaded=!0}catch(e){throw new Error(e)}}async stream(){if(this.ready)return;const e=524288,t=(await fetch(this.videoDir+this.videoData.fileName)).body.getReader(),i=new ReadableStream({async pull(i){let{done:a,value:o}=await t.read();if(a)return i.close();for(let t=0;t<o.length;t+=e)i.enqueue(o.slice(t,t+e))}}),a=await new Response(i).blob(),o=URL.createObjectURL(a);this.video.src=o}async initVideo(){this.ready||(this.loaded||await this.loadMeta(),await this.stream(),this.video.poster=this.videoDir+"poster.jpg",this.video.load(),this.ready=!0)}}document.addEventListener("DOMContentLoaded",(()=>new VideoPlayer));