class VideoPlayer{constructor(){this.container=document.getElementById("player"),this.video=this.container.querySelector("video"),this.controls=this.initControls()}initControls(){const t={};return this.container.querySelectorAll(".player-controls [action]").forEach((e=>{t[e.getAttribute("action")]=e})),t}}document.addEventListener("DOMContentLoaded",(()=>new VideoPlayer));