class VideoPlayer{constructor(){this.player=document.querySelector(".video-wrapper"),this.videoId=this.player.getAttribute("videoId"),this.videoData={},this.loaded=!1,this.loadMeta()}async loadMeta(){try{const t=await fetch("/api/video/"+this.videoId,{method:"get",headers:{"Content-Type":"application/json"}});if(!t.ok)throw new Error(t.status);this.videoData=await t.json(),this.loaded=!0}catch(t){throw new Error(t)}}}document.addEventListener("DOMContentLoaded",(()=>new VideoPlayer));