class VideoPlayer{constructor(){this.container=document.getElementById("player"),this.video=this.container.querySelector("video"),this.controls=this.initControls(),this.initEventHandlers()}initControls(){const t={};return this.container.querySelectorAll(".player-controls [action]").forEach((e=>{t[e.getAttribute("action")]=e})),t}initEventHandlers(){this.controls.play.addEventListener("click",this.play.bind(this)),this.controls.pause.addEventListener("click",this.pause.bind(this))}async play(){await this.video.play(),this.controls.play.disabled=!0,this.controls.pause.disabled=!1}pause(){this.video.pause(),this.controls.play.disabled=!1,this.controls.pause.disabled=!0}}document.addEventListener("DOMContentLoaded",(()=>new VideoPlayer));