const deleteList=async e=>{await fetch(`/api/list/${e}/delete`,{method:"post"}),location.reload()},renameList=async e=>{await fetch(`/api/list/${e}/rename`,{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:prompt("Playlist name:","Untitled")})}),location.reload()};document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll('[list][action="delete"]').forEach((e=>e.addEventListener("click",(()=>deleteList(e.getAttribute("list")))))),document.querySelectorAll('[list][action="rename"]').forEach((e=>e.addEventListener("click",(()=>renameList(e.getAttribute("list"))))))}));