class VideoUploader{constructor(){this.container=document.querySelector(".upload-container"),this.dropArea=this.container.querySelector(".file-drop-area"),this.fileInput=this.container.querySelector('[name="video"]'),this.preview=this.container.querySelector(".video-preview"),this.previewPlayer=this.preview.querySelector(".preview-player"),this.initEventHandlers()}initEventHandlers(){["dragenter","dragover","dragleave","drop"].forEach((e=>{this.dropArea.addEventListener(e,(e=>{e.preventDefault(),e.stopImmediatePropagation(),e.stopPropagation()}))})),["dragenter","dragover"].forEach((e=>{this.dropArea.addEventListener(e,(()=>{this.dropArea.classList.add("dragover")}))})),["dragleave","drop"].forEach((e=>{this.dropArea.addEventListener(e,(()=>{this.dropArea.classList.remove("dragover")}))})),this.dropArea.addEventListener("drop",(e=>{const t=e.dataTransfer.files[0];t&&t.type.startsWith("video/")&&this.handleFileSelect(t)})),this.fileInput.addEventListener("change",(e=>{const t=e.target.files[0];t&&this.handleFileSelect(t)})),this.container.addEventListener("submit",(e=>{e.preventDefault(),e.stopImmediatePropagation(),e.stopPropagation(),this.handleUpload()}))}handleFileSelect(e){this.preview.querySelector(".file-name").textContent=e.name;const t=URL.createObjectURL(e);this.previewPlayer.src=t,this.previewPlayer.load(),this.dropArea.classList.add("hidden"),this.preview.classList.remove("hidden")}async handleUpload(){}}document.addEventListener("DOMContentLoaded",(()=>new VideoUploader));